try {
  // 1️⃣ 取得 replyToken
  const replyToken = $('Webhook').first().json?.body?.events?.[0]?.replyToken;
  if (!replyToken) throw new Error('找不到 replyToken');

  // 2️⃣ 取得 AI 模型的 output 內容
  const outputContent = $input.first().json?.output || $input.first().json.answer || $input.first().json.result;
  let text = '';

  if (typeof outputContent === 'string') {
    // 直接是字串
    text = outputContent;

  } else if (Array.isArray(outputContent)) {
    // 如果是 ["字串", "字串", ...]
    if (outputContent.every(item => typeof item === 'string')) {
      text = outputContent.join('\n');

    // 如果是 [{type:'text', text:'...'}]
    } else if (outputContent.every(item => typeof item === 'object' && item?.type === 'text' && item?.text)) {
      text = outputContent.map(item => item.text).join('');

    // 如果是 [{output: [...]}]
    } else if (outputContent.length > 0 && outputContent[0]?.output) {
      const inner = outputContent[0].output;
      if (Array.isArray(inner)) {
        text = inner.join('\n');
      } else if (typeof inner === 'string') {
        text = inner;
      } else {
        throw new Error('內部 output 格式不正確');
      }

    } else {
      throw new Error('無法識別的 output 陣列格式');
    }

  } else {
    throw new Error('output 格式不正確');
  }

  if (!text || text.trim().length === 0) throw new Error('處理後的文字內容為空');

  // 3️⃣ 文字清理
  text = text
    .trim()
    .replace(/\r\n|\r/g, '\n')                 // 統一換行
    .replace(/[\u0000-\u0008\u000B-\u001F]/g, ''); // 移除危險控制字符

  // 4️⃣ 超長文字自動分段，每段 < 5000 字
  const segments = [];
  let remaining = text;
  const MAX_LENGTH = 4950; // LINE API 上限為 5000，保留一些餘裕

  while (remaining.length > 0) {
    if (remaining.length <= MAX_LENGTH) {
      segments.push(remaining);
      break;
    }

    let segment = remaining.substring(0, MAX_LENGTH);
    // 優先在換行符處切分，其次是空格，以保持句子完整性
    const cutPoint = Math.max(segment.lastIndexOf('\n'), segment.lastIndexOf(' '));

    // 如果找不到好的切分點，就直接硬切
    segment = (cutPoint > 0) ? segment.substring(0, cutPoint) : segment;

    segments.push(segment);
    remaining = remaining.substring(segment.length).trim();
  }

  // 5️⃣ 組合 LINE Message 物件陣列
  const messages = segments.map(seg => ({
    type: 'text',
    text: seg.length > 0 ? seg : '...(內容已截斷)'
  }));

  // LINE Reply API 一次最多只能回覆 5 則訊息，所以只取前 5 則
  const finalMessages = messages.slice(0, 5);

  // ✨ 返回一個包含 replyToken 和 messages 的物件
  return [{
    json: {
      replyToken: replyToken,
      messages: finalMessages
    }
  }];

} catch (error) {
  // 發生錯誤時，也回傳同樣的結構，方便 LINE 節點處理
  console.error('處理數據時發生錯誤:', error.message);

  const replyToken = $('Webhook').first().json?.body?.events?.[0]?.replyToken || 'unknown';

  return [{
    json: {
      replyToken: replyToken,
      messages: [{
        type: 'text',
        text: `抱歉，系統在處理您的訊息時發生錯誤，請稍後再試。\n錯誤資訊：${error.message}`
      }]
    }
  }];
}

// 取得使用者輸入的訊息
let text = $json.body.events[0].message.text || "";

// 指令對應表
const mapping = {
  "@ITR": "ITR Agent",
  "@FACT": "FACT Agent",
  "@NEWS": "NEWS Agent",
  "@DR": "DR Agent",
  "@STOCK": "Stock Module",
  "@CB": "CB Module",
  "@WEB": "Web Module",
  "@SUBS": "Subscription Module",
  "@EAT": "Eat Module",
  "@IMG": "Image Module",
  "@help": "Help Handler",
  "@GIMG": "Image Generating",
  "@EIMG": "Image Edit",
  "@SHD": "Shadow",
  "@M365": "M365 Agent",
  "@1399": "1399 Agent",
  "@itr": "ITR roadshow",
  "@HELP": "Help Handler",
};

// 預設路由
let route = "Default";

// 遍歷 mapping，找到符合的指令，並移除該關鍵字
for (const key of Object.keys(mapping)) {
  if (text.includes(key)) {
    route = mapping[key];
    text = text.replace(key, "").trim();  // 移除關鍵字，但保留網址
    break;
  }
}

// 嘗試提取 URL（支援 http/https）
let url = null;
const urlRegex = /(https?:\/\/[^\s]+)/g;
const match = text.match(urlRegex);
if (match && match.length > 0) {
  url = match[0];  // 取第一個網址
  // ❌ 不要從 text 移除網址，避免 userInput 變空
}

// 回傳統一格式
return [
  {
    json: {
      userInput: text.trim(),
      route: route,
      url: url,
      messageId: $input.first().json.body.events[0].message.id,
    }
  }
];

try {
  // 1️⃣ 取得 replyToken
  const replyToken = $('Webhook').first().json?.body?.events?.[0]?.replyToken;
  if (!replyToken) throw new Error('找不到 replyToken');

  // 2️⃣ 取得輸入資料
  const input = $input.first().json;

  let messages = [];

  // 2a️⃣ 如果有 image_url，回傳 LINE 圖片訊息
  if (input.image_url) {
    messages.push({
      type: 'image',
      originalContentUrl: input.image_url,
      previewImageUrl: input.image_url
    });
  }

  // 2b️⃣ 如果有文字內容 (output / answer / result)
  const outputContent = input.output || input.answer || input.result;
  if (outputContent) {
    let text = '';

    if (typeof outputContent === 'string') {
      text = outputContent;

    } else if (Array.isArray(outputContent)) {
      if (outputContent.every(item => typeof item === 'string')) {
        text = outputContent.join('\n');
      } else if (outputContent.every(item => typeof item === 'object' && item?.type === 'text' && item?.text)) {
        text = outputContent.map(item => item.text).join('');
      } else if (outputContent.length > 0 && outputContent[0]?.output) {
        const inner = outputContent[0].output;
        text = Array.isArray(inner) ? inner.join('\n') : (typeof inner === 'string' ? inner : '');
      }
    }

    if (text && text.trim().length > 0) {
      // 分段處理
      const segments = [];
      let remaining = text.trim();
      const MAX_LENGTH = 4950;

      while (remaining.length > 0) {
        if (remaining.length <= MAX_LENGTH) {
          segments.push(remaining);
          break;
        }
        let segment = remaining.substring(0, MAX_LENGTH);
        const cutPoint = Math.max(segment.lastIndexOf('\n'), segment.lastIndexOf(' '));
        segment = (cutPoint > 0) ? segment.substring(0, cutPoint) : segment;
        segments.push(segment);
        remaining = remaining.substring(segment.length).trim();
      }

      messages.push(...segments.map(seg => ({
        type: 'text',
        text: seg.length > 0 ? seg : '...(內容已截斷)'
      })));
    }
  }

  if (messages.length === 0) throw new Error('沒有可用的輸出內容');

  // 限制 LINE Reply API 一次最多 5 則訊息
  const finalMessages = messages.slice(0, 5);

  return [{
    json: {
      replyToken: replyToken,
      messages: finalMessages
    }
  }];

} catch (error) {
  console.error('處理數據時發生錯誤:', error.message);
  const replyToken = $('Webhook').first().json?.body?.events?.[0]?.replyToken || 'unknown';

  return [{
    json: {
      replyToken: replyToken,
      messages: [{
        type: 'text',
        text: `抱歉，系統在處理您的訊息時發生錯誤，請稍後再試。\n錯誤資訊：${error.message}`
      }]
    }
  }];
}
